package com.sentax.cls07;

public class LoopPrac3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 100;
		while(num>=1) {
			
			if (num%2!=0) {System.out.print(num + " ");}
		num--;}
		System.out.println("***************************************");
		int a = 100;
		while(a>=1) {
			
			if (a%2!=0) {System.out.print(a + " ");}
		a-=1;}

	}

}
