package com.syntax.cls09;

public class ForLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1;i<4;i++) {
			for (int j=0;j<=2;j++) {
				System.out.println("hello");
			break;
			}
			
			System.out.println("bye");
			break;
			
			
			
		}
			

	}

}
