package com.sytax.cls08;

public class NestedLoopCar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//car 
        for(int a=0; a<=9; a++)  {

            for(int b=0; b<=9; b++) {

                for (int c=0; c<=9; c++) {

                    System.out.println(a+" "+b+" "+c);
                }
            }
        }

        

	}

}
